import React from "react";
import axios from "axios";
import { useState } from "react";

function Weather(props) {
  const { weather } = props;
  const [weatherfour, setWeatherfour] = useState();
  const fetchfour = async (e) => {
    const { lat, lon } = weather?.coord;
    const response2 = await axios.get(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=9cacdfdab391b52dc15c9197b29942eb
      `
    );
    setWeatherfour(response2.data);
  };
  const getTime = (timeStamp) => {
    return `${new Date(timeStamp * 1000).getHours()} : ${new Date(
      timeStamp * 1000
    ).getMinutes()}`;
  };
  return (
    <>
      <img
        src={`https://openweathermap.org/img/wn/${weather?.weather[0].icon}@2x.png`}
        alt=""
      />
      <h3>
        {`${Math.floor(weather?.main.temp - 273)}C`}{" "}
        <span>{weather?.weather[0].main}</span>
      </h3>
      <h3>{`${weather?.name}, ${weather?.sys.country}`}</h3>
      <h3>Weather Information</h3>
      <h4>{`coordinates: ${weather?.coord.lon}, ${weather?.coord.lat}`}</h4>
      <h4>{`sunrise: ${getTime(weather?.sys.sunrise)}, sunset: ${getTime(
        weather?.sys.sunset
      )}`}</h4>
      <h4>{`Min Temp: ${Math.floor(
        weather?.main.temp_min - 273
      )},Max Temp: ${Math.floor(weather?.main.temp_max - 273)} `}</h4>
      {fetchfour()}
    </>
  );
}

export { Weather };
