import "./App.css";
import { useState } from "react";
import City from "./components/City";
import Weather from "./components/Weather";
import WeatherNext from "./components/WeatherNext";
import axios from "axios";

function App() {
  const [city, setCity] = useState();
  const [weather, setWeather] = useState();
  const [weatherfour, setWeatherfour] = useState();
  const fetch = async (e) => {
    e.preventDefault();
    const response = await axios.get(
      `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=9cacdfdab391b52dc15c9197b29942eb`
    );
    setWeather(response.data);
    const lat = weather?.coord.lat;
    const lon = weather?.coord.lon;

    const response2 = await axios.get(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=9cacdfdab391b52dc15c9197b29942eb
      `
    );
    setWeatherfour(response2.data);
  };
  return (
    <div className="container">
      <h1>HI</h1>

      <City className="city" setCity={setCity} fetch={fetch}></City>
      <Weather className="weather" weather={weather}></Weather>
      <h3>{weatherfour?.weather.description}</h3>
      <h3>Hi</h3>
      {/* <WeatherNext weather={weatherfour}></WeatherNext> */}
    </div>
  );
}

export default App;
